function toast({title:title="",message:message="",type:type="info",duration:duration=3e3}){const main=document.getElementById("toast");if(main){const toast=document.createElement("div"),autoRemoveId=setTimeout((function(){main.removeChild(toast)}),duration+3e3);toast.onclick=function(e){e.target.closest(".toast__close")&&(main.removeChild(toast),clearTimeout(autoRemoveId))};const icons={success:"fa-circle-check",info:"fa-circle-info",warning:"fa-triangle-exclamation",error:"fa-circle-exclamation"},icon=icons[type],delay=(duration/1e3).toFixed(2);toast.classList.add("toast",`toast--${type}`),toast.style.animation=`slideInLeft ease .4s , fadeOut linear 1.5s ${delay}s forwards`,toast.innerHTML=`\n          <div class="toast__icon">\n              <i class="fa-solid ${icon}"></i>\n          </div>\n          <div class="toast__body">\n              <h3 class="toast__title">${title}</h3>\n              <p class="toast__msg">${message}</p>\n          </div>\n          <div class="toast__close">\n              <i class="fa-solid fa-xmark"></i>\n          </div>\n      `,main.appendChild(toast)}}